						newActiveStepIndex = activeStepIndex - 1;
						stepView = carouselView.stepViews[newActiveStepIndex];
						stepView.toggleActiveStep();
						this.oCoreApi.setActiveStep(this.oCoreApi.getSteps()[newActiveStepIndex]);
