	this._oButton = new sap.m.Button(this.getId() + "-add", {
		press: fireCaptureInput,
		icon: "sap-icon://add",
		type: sap.m.ButtonType.Transparent,
		width: "100%",
		enabled: true
	}).setParent(this).addStyleClass("sapCaUiAddPictureButton");

