    var fnAbortXhr = oLineItem.getModel().getProperty(sPath + "/abortUplXhr");
    var fnAbort = oLineItem.getModel().getProperty(sPath + "/abortUpl");
    fnAbortXhr && fnAbortXhr();
    fnAbort && fnAbort();
    var aTokens = sPath.split('/');
