			var item = mSeriesColor[i];
			if (item.fields.length === bindingIds.length){
				var bFound = true;
				for (var j = 0; bFound && j < bindingIds.length; ++j){
					bFound = (bindingIds[j] === item.fields[j]);
				}
				if (bFound) {
					seriesColor = item;
				}
			}
