sap.ui.jsview("sap.collaboration.components.socialprofile.SocialProfile",{getControllerName:function(){return"sap.collaboration.components.socialprofile.SocialProfile";},createContent:function(c){this._sPrefixId=this.getId();this._oVBox=new sap.m.VBox(this._sPrefixId+"_VBox").addStyleClass("vbox");this._createVBoxContent();return this._oVBox;},_createVBoxContent:function(){this._oVBox.addItem(this._createHBoxHeader());var c=new sap.m.Label(this._sPrefixId+"_ContactDetailsLabel",{text:this.getViewData().langBundle.getText("SP_CONTACT_DETAILS_LABEL")}).addStyleClass("heading");var m=new sap.m.Text(this._sPrefixId+"_MobileNumber",{text:"{/MemberProfile/MobilePhoneNumber}"});var M=new sap.m.Label(this._sPrefixId+"_MobileLabel",{text:this.getViewData().langBundle.getText("SP_MOBILE_LABEL"),labelFor:m.getId()}).addStyleClass("label");var w=new sap.m.Text(this._sPrefixId+"_WorkNumber",{text:"{/MemberProfile/WorkPhoneNumber}"});var W=new sap.m.Label(this._sPrefixId+"_WorkLabel",{text:this.getViewData().langBundle.getText("SP_WORK_LABEL"),labelFor:w.getId()}).addStyleClass("label");var e=new sap.m.Link(this._sPrefixId+"_Email",{text:"{/Email}",press:function(){this.setHref("mailto:"+this.getText());}});var E=new sap.m.Label(this._sPrefixId+"_EmailLabel",{text:this.getViewData().langBundle.getText("SP_EMAIL_LABEL"),labelFor:e.getId()}).addStyleClass("label");var C=new sap.m.Label(this._sPrefixId+"_CompanyDetailsLabel",{text:this.getViewData().langBundle.getText("SP_COMPANY_DETAILS_LABEL")}).addStyleClass("heading");var o=new sap.m.Text(this._sPrefixId+"_CompanyAddress",{text:"{/MemberProfile/Address}"});var a=new sap.m.Label(this._sPrefixId+"_CompanyAddressLabel",{text:this.getViewData().langBundle.getText("SP_COMPANY_ADDRESS_LABEL"),labelFor:o.getId()}).addStyleClass("label");this._oVBox.addItem(c).addItem(M).addItem(m).addItem(W).addItem(w).addItem(E).addItem(e).addItem(C).addItem(a).addItem(o);},_createHBoxHeader:function(){var h=new sap.m.VBox(this._sPrefixId+"_HeaderVBox").addStyleClass("headervbox");var f=new sap.m.Text(this._sPrefixId+"_FullName",{text:"{/FullName}",width:"200px",maxLines:1}).addStyleClass("fullname");var r=new sap.m.Text(this._sPrefixId+"_Role",{text:"{/Title}",width:"200px",maxLines:1}).addStyleClass("role");h.addItem(f).addItem(r);var H=new sap.m.HBox(this._sPrefixId+"_HeaderHBox",{height:"48px"});var u=new sap.m.Image(this._sPrefixId+"_HeaderUserImage",{src:"{/UserImage}",alt:"{/FullName}",width:"48px",height:"48px"}).addStyleClass("image");var n=new sap.m.ObjectStatus(this._sPrefixId+"_NoUserHeader",{text:this.getViewData().langBundle.getText("SP_NO_USER"),state:"Warning",icon:"sap-icon://alert",visible:false});H.addItem(u).addItem(h).addItem(n);return H;},resetHeader:function(){var n=sap.ui.getCore().byId(this._sPrefixId+"_NoUserHeader");if(n.getVisible()===true){n.setVisible(false);sap.ui.getCore().byId(this._sPrefixId+"_HeaderVBox").setVisible(true);sap.ui.getCore().byId(this._sPrefixId+"_HeaderUserImage").setVisible(true);}},setHeaderNoUser:function(){var u=sap.ui.getCore().byId(this._sPrefixId+"_HeaderUserImage");if(u.getVisible()===true){u.setVisible(false);sap.ui.getCore().byId(this._sPrefixId+"_HeaderVBox").setVisible(false);sap.ui.getCore().byId(this._sPrefixId+"_NoUserHeader").setVisible(true);}}});
