		var sODataEndPoint;
		if(!sFolderId && sGroupId) {
			sODataEndPoint = "/" + this.OdataUtilConstants.EndPoint.Groups + "('" + jQuery.sap.encodeURL(sGroupId) + "')/" + this.OdataUtilConstants.EndPoint.Folders;
		}
