	GanttChart.prototype.setShapes = function (aShapes) {
		this.setProperty("shapes", aShapes, true);
		if (aShapes && aShapes.length > 0) {
			this._oShapeManager.instantiateShapes(aShapes);
			this._draw(true/**bForce*/);
		}
		return this;
	};

