(function(){"use strict";sap.ui.controller("sap.ovp.cards.charts.line.LineChart",{onInit:function(){sap.ovp.cards.charts.VizAnnotationManager.formatChartAxes();},onBeforeRendering:function(){sap.ovp.cards.charts.VizAnnotationManager.validateCardConfiguration(this);var v=this.getView().byId("lineChartCard");var n;if(!v){jQuery.sap.log.error(sap.ovp.cards.charts.Utils.constants.ERROR_NO_CHART+": ("+this.getView().getId()+")");}else{n=v.getModel('ovpCardProperties').getProperty("/navigation");if(n=="chartNav"){v.attachBrowserEvent("click",this.onHeaderClick.bind(this));}else{sap.ovp.cards.charts.VizAnnotationManager.getSelectedDataPoint(v,this);}var b=v.getDataset().getBinding("data");if(b.getPath()){b.attachDataReceived(jQuery.proxy(this.onDataReceived,this));b.attachDataRequested(jQuery.proxy(this.onDataRequested,this));}else{var a=sap.ui.xmlfragment("sap.ovp.cards.charts.generic.noData");var c=this.getCardContentContainer();c.removeAllItems();c.addItem(a);}sap.ovp.cards.charts.Utils.validateMeasuresDimensions(v,"Line");}},onDataReceived:function(e){var v=this.getView().byId("lineChartCard");if(this.bFlag==true){this.bFlag=false;}else{setTimeout(function(){v.setBusy(false);},0);}sap.ovp.cards.charts.VizAnnotationManager.hideDateTimeAxis(v,"categoryAxis");sap.ovp.cards.charts.VizAnnotationManager.checkNoData(e,this.getCardContentContainer(),v);},onDataRequested:function(){var v=this.getView().byId("lineChartCard");v.setBusy(true);}});})();
