tinyMCEPopup.requireLangPack();var PasteTextDialog={init:function(){this.resize();},insert:function(){var h=tinyMCEPopup.dom.encode(document.getElementById('content').value),l;if(document.getElementById('linebreaks').checked){l=h.split(/\r?\n/);if(l.length>1){h='';tinymce.each(l,function(r){h+='<p>'+r+'</p>';});}}tinyMCEPopup.editor.execCommand('mceInsertClipboardContent',false,{content:h});tinyMCEPopup.close();},resize:function(){var v=tinyMCEPopup.dom.getViewPort(window),e;e=document.getElementById('content');e.style.width=(v.w-20)+'px';e.style.height=(v.h-90)+'px';}};tinyMCEPopup.onInit.add(PasteTextDialog.init,PasteTextDialog);
