tinyMCEPopup.requireLangPack();var PasteWordDialog={init:function(){var e=tinyMCEPopup.editor,a=document.getElementById('iframecontainer'),i,d,c,b='';a.innerHTML='<iframe id="iframe" src="javascript:\'\';" frameBorder="0" style="border: 1px solid gray"></iframe>';i=document.getElementById('iframe');d=i.contentWindow.document;c=[e.baseURI.toAbsolute("themes/"+e.settings.theme+"/skins/"+e.settings.skin+"/content.css")];c=c.concat(tinymce.explode(e.settings.content_css)||[]);tinymce.each(c,function(u){b+='<link href="'+e.documentBaseURI.toAbsolute(''+u)+'" rel="stylesheet" type="text/css" />';});d.open();d.write('<html><head>'+b+'</head><body class="mceContentBody" spellcheck="false"></body></html>');d.close();d.designMode='on';this.resize();window.setTimeout(function(){i.contentWindow.focus();},10);},insert:function(){var h=document.getElementById('iframe').contentWindow.document.body.innerHTML;tinyMCEPopup.editor.execCommand('mceInsertClipboardContent',false,{content:h,wordContent:true});tinyMCEPopup.close();},resize:function(){var v=tinyMCEPopup.dom.getViewPort(window),e;e=document.getElementById('iframe');if(e){e.style.width=(v.w-20)+'px';e.style.height=(v.h-90)+'px';}}};tinyMCEPopup.onInit.add(PasteWordDialog.init,PasteWordDialog);
