tinyMCEPopup.requireLangPack();
function init(){var e,t;tinyMCEPopup.resizeToInnerSize();e=tinyMCEPopup.editor;window.setTimeout(insertHelpIFrame,10);t=document.getElementById('plugintablecontainer');document.getElementById('plugins_tab').style.display='none';var h="";h+='<table id="plugintable">';h+='<thead>';h+='<tr>';h+='<td>'+e.getLang('advanced_dlg.about_plugin')+'</td>';h+='<td>'+e.getLang('advanced_dlg.about_author')+'</td>';h+='<td>'+e.getLang('advanced_dlg.about_version')+'</td>';h+='</tr>';h+='</thead>';h+='<tbody>';tinymce.each(e.plugins,function(p,n){var i;if(!p.getInfo)return;h+='<tr>';i=p.getInfo();if(i.infourl!=null&&i.infourl!='')h+='<td width="50%" title="'+n+'"><a href="'+i.infourl+'" target="_blank">'+i.longname+'</a></td>';else h+='<td width="50%" title="'+n+'">'+i.longname+'</td>';if(i.authorurl!=null&&i.authorurl!='')h+='<td width="35%"><a href="'+i.authorurl+'" target="_blank">'+i.author+'</a></td>';else h+='<td width="35%">'+i.author+'</td>';h+='<td width="15%">'+i.version+'</td>';h+='</tr>';document.getElementById('plugins_tab').style.display='';});h+='</tbody>';h+='</table>';t.innerHTML=h;tinyMCEPopup.dom.get('version').innerHTML=tinymce.majorVersion+"."+tinymce.minorVersion;tinyMCEPopup.dom.get('date').innerHTML=tinymce.releaseDate;}
function insertHelpIFrame(){var h;if(tinyMCEPopup.getParam('docs_url')){h='<iframe width="100%" height="300" src="'+tinyMCEPopup.editor.baseURI.toAbsolute(tinyMCEPopup.getParam('docs_url'))+'"></iframe>';document.getElementById('iframecontainer').innerHTML=h;document.getElementById('help_tab').style.display='block';document.getElementById('help_tab').setAttribute("aria-hidden","false");}}
tinyMCEPopup.onInit.add(init);
