tinymce.PluginManager.add('example',function(a,u){a.addButton('example',{text:'My button',icon:false,onclick:function(){a.windowManager.open({title:'Example plugin',body:[{type:'textbox',name:'title',label:'Title'}],onsubmit:function(e){a.insertContent('Title: '+e.data.title);}});}});a.addMenuItem('example',{text:'Example plugin',context:'tools',onclick:function(){a.windowManager.open({title:'TinyMCE site',url:u+'/dialog.html',width:600,height:400,buttons:[{text:'Insert',onclick:function(){var w=a.windowManager.getWindows()[0];a.insertContent(w.getContentWindow().document.getElementById('content').value);w.close();}},{text:'Close',onclick:'close'}]});}});});
