			if (jQuery.sap.endsWith(r.name, ".dae")) {
				this._resourceFiles[r.name] = resourceToFile(r.value, r.name);
			} else {
				this._resourceFiles[r.name] = "data:" + r.name.split(".")[1] + ";base64," + r.value;
			}
