sap.ui.define([],function(){"use strict";function M(){var s=null;this.init=function(S){s=S;return this;};this.show=function(t,m,p){if(!m){jQuery.sap.log.error("Message must not be empty.");}else{if(s){s(t,m,p||{});}else{jQuery.sap.log.error("MessageService not initialized. [Message: '"+m+"']");}}};this.info=function(m,d){this.show(M.Type.INFO,m,{duration:d||3000});};this.error=function(m,t){m=(t!==undefined)?t+" , "+m:m;jQuery.sap.log.error(m);this.show(M.Type.ERROR,m,{title:t});};this.confirm=function(m,c,t,a){this.show(M.Type.CONFIRM,m,{title:t,callback:c,actions:a});};};M.Type={INFO:0,ERROR:1,CONFIRM:2};M.hasNoAdapter=true;return M;},true);
